package forloop;

public class OddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i=25;i<=50;i=i+2)
		
			if((i/2)!=0) {
				System.out.println( i +" number is odd ");}
				else {
					System.out.println( i +" number is odd ");
					
			}
		}

	}


